import React, { useState } from 'react';
import { useCart } from '../context/CartContext';

const products = [
  { id: 1, name: 'Classic White Shirt', category: 'Men', price: 1999, color: 'White', image: 'https://via.placeholder.com/300x400' },
  { id: 2, name: 'Silk Blouse', category: 'Women', price: 2499, color: 'Black', image: 'https://via.placeholder.com/300x400' },
  { id: 3, name: 'Denim Jacket', category: 'Men', price: 2999, color: 'Blue', image: 'https://via.placeholder.com/300x400' },
  { id: 4, name: 'Wrap Dress', category: 'Women', price: 2799, color: 'Red', image: 'https://via.placeholder.com/300x400' },
  { id: 5, name: 'Oversized Tee', category: 'Men', price: 1299, color: 'Black', image: 'https://via.placeholder.com/300x400' },
  { id: 6, name: 'Floral Midi Dress', category: 'Women', price: 3199, color: 'Blue', image: 'https://via.placeholder.com/300x400' },
];

const ProductList = () => {
  const [filter, setFilter] = useState('All');
  const [colorFilter, setColorFilter] = useState('All');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const { addToCart } = useCart();

  const handleAddToCart = (product) => {
    addToCart(product);
  };

  const filteredProducts = products.filter((product) => {
    const matchesCategory = filter === 'All' || product.category === filter;
    const matchesColor = colorFilter === 'All' || product.color === colorFilter;
    const matchesPrice = (!minPrice || product.price >= parseInt(minPrice)) &&
                         (!maxPrice || product.price <= parseInt(maxPrice));
    return matchesCategory && matchesColor && matchesPrice;
  });

  return (
    <div className="px-6 py-10 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-10 text-gray-800">Luxor Collection</h1>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 justify-center mb-10">
        <select
          className="px-4 py-2 rounded border text-gray-700"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="All">All</option>
          <option value="Men">Men</option>
          <option value="Women">Women</option>
        </select>

        <select
          className="px-4 py-2 rounded border text-gray-700"
          value={colorFilter}
          onChange={(e) => setColorFilter(e.target.value)}
        >
          <option value="All">All Colors</option>
          <option value="White">White</option>
          <option value="Black">Black</option>
          <option value="Blue">Blue</option>
          <option value="Red">Red</option>
        </select>

        <input
          type="number"
          placeholder="Min Price"
          className="px-4 py-2 rounded border text-gray-700"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />

        <input
          type="number"
          placeholder="Max Price"
          className="px-4 py-2 rounded border text-gray-700"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {filteredProducts.map((product) => (
          <div
            key={product.id}
            className="bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-2xl transition duration-300"
          >
            <img src={product.image} alt={product.name} className="w-full h-72 object-cover" />
            <div className="p-4">
              <h2 className="text-lg font-semibold text-gray-800 mb-1">{product.name}</h2>
              <p className="text-sm text-gray-500 mb-1">{product.category} • {product.color}</p>
              <p className="text-sm text-gray-700 font-semibold mb-3">₹{product.price.toLocaleString()}</p>
              <button
                onClick={() => handleAddToCart({ ...product, quantity: 1 })}
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition"
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
