import React from 'react';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';

const CartPage = () => {
  const { cartItems, dispatch } = useCart();

  const handleRemove = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 tracking-wider">Your Shopping Bag</h1>

      {cartItems.length === 0 ? (
        <div className="text-gray-600">
          Your cart is empty. <Link to="/products" className="text-black underline">Shop now</Link>
        </div>
      ) : (
        <div className="space-y-6">
          {cartItems.map(item => (
            <div key={item._id} className="flex items-center justify-between bg-white p-4 rounded-xl shadow-md">
              <div className="flex items-center gap-4">
                <img src={item.image} alt={item.name} className="w-20 h-20 rounded-lg object-cover" />
                <div>
                  <h2 className="text-lg font-semibold">{item.name}</h2>
                  <p className="text-sm text-gray-500">Qty: {item.quantity}</p>
                  <p className="text-sm text-gray-700">
                    ₹{item.price} × {item.quantity} = <strong>₹{item.price * item.quantity}</strong>
                  </p>
                </div>
              </div>
              <button
                onClick={() => handleRemove(item._id)}
                className="text-red-500 hover:underline"
              >
                Remove
              </button>
            </div>
          ))}

          <div className="flex justify-between items-center border-t pt-4">
            <h2 className="text-xl font-semibold">Subtotal:</h2>
            <span className="text-xl font-bold">₹{total.toFixed(2)}</span>
          </div>

          <div className="text-right">
            <button className="bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800 transition">
              Proceed to Checkout
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
