import React from 'react';
import { useCart } from '../context/CartContext';
import { Link, useNavigate } from 'react-router-dom';

const CartPage = () => {
  const { cartItems, dispatch } = useCart();
  const navigate = useNavigate();

  const handleRemove = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleCheckout = () => {
    // Replace with Razorpay/Stripe logic later
    alert('ðŸ§¾ Proceeding to checkout...');
    navigate('/checkout'); // If you plan to add a separate Checkout page
  };

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 tracking-wider">Your Shopping Bag</h1>

      {cartItems.length === 0 ? (
        <div className="text-gray-600">
          Your cart is empty. <Link to="/products" className="text-black underline">Shop now</Link>
        </div>
      ) : (
        <div className="space-y-6">
          {cartItems.map(item => (
            <div key={item._id} className="flex flex-col sm:flex-row justify-between items-center bg-white p-4 rounded-xl shadow-md">
              <div className="flex items-center gap-4">
                <img
                  src={item.image || 'https://via.placeholder.com/100'}
                  alt={item.name}
                  className="w-24 h-24 object-cover rounded-lg border"
                />
                <div>
                  <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                  <p className="text-sm text-gray-500">Qty: {item.quantity}</p>
                  <p className="text-sm text-gray-700">
                    â‚¹{item.price.toLocaleString()} Ã— {item.quantity} = <strong>â‚¹{(item.price * item.quantity).toLocaleString()}</strong>
                  </p>
                </div>
              </div>
              <button
                onClick={() => handleRemove(item._id)}
                className="mt-4 sm:mt-0 text-red-500 hover:underline"
              >
                Remove
              </button>
            </div>
          ))}

          <div className="flex justify-between items-center border-t pt-4 text-lg font-medium">
            <h2>Subtotal:</h2>
            <span className="font-bold text-xl text-gray-900">â‚¹{total.toLocaleString()}</span>
          </div>

          <div className="text-right">
            <button
              onClick={handleCheckout}
              className="bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800 transition"
            >
              Proceed to Checkout
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default CartPage;
