import React from 'react';

const orders = [
  {
    id: 'ORD123456',
    date: '2025-06-01',
    total: 'Rs. 3,499',
    status: 'Delivered',
    items: ['Beige Oversized Hoodie', 'Slim Fit Jeans'],
  },
  {
    id: 'ORD123457',
    date: '2025-05-20',
    total: 'Rs. 1,899',
    status: 'Shipped',
    items: ['Graphic T-Shirt'],
  },
];

const OrderHistory = () => {
  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-center">Order History</h1>
      {orders.map((order) => (
        <div
          key={order.id}
          className="bg-white shadow-md rounded-xl p-4 mb-6 border border-gray-200"
        >
          <div className="flex justify-between items-center mb-2">
            <h2 className="font-semibold text-lg">Order #{order.id}</h2>
            <span className="text-sm text-gray-600">{order.date}</span>
          </div>
          <div className="mb-2">
            <p className="text-gray-700">
              <span className="font-medium">Items:</span>{' '}
              {order.items.join(', ')}
            </p>
            <p className="text-gray-700">
              <span className="font-medium">Total:</span> {order.total}
            </p>
            <p className="text-gray-700">
              <span className="font-medium">Status:</span>{' '}
              <span
                className={
                  order.status === 'Delivered'
                    ? 'text-green-600'
                    : 'text-yellow-600'
                }
              >
                {order.status}
              </span>
            </p>
          </div>
          <button className="mt-2 text-sm font-medium text-black underline">
            View Details
          </button>
        </div>
      ))}
    </div>
  );
};

export default OrderHistory;
