// src/pages/OrderHistory.js
import React from 'react';

const orders = [
  {
    id: 'ORD123456',
    date: '2025-06-10',
    items: ['White Shirt', 'Black Jeans'],
    total: '₹3,499',
    status: 'Delivered',
  },
  {
    id: 'ORD123457',
    date: '2025-06-05',
    items: ['Oversized Hoodie'],
    total: '₹2,199',
    status: 'In Progress',
  },
];

const OrderHistory = () => {
  return (
    <div>
      <h2 className="text-2xl font-bold mb-6">Order History</h2>
      <div className="space-y-4">
        {orders.map((order) => (
          <div key={order.id} className="bg-white p-5 rounded-lg shadow-sm">
            <div className="flex justify-between mb-2">
              <span className="font-medium">Order ID: {order.id}</span>
              <span className="text-sm text-gray-500">{order.date}</span>
            </div>
            <p className="text-gray-700">
              <strong>Items:</strong> {order.items.join(', ')}
            </p>
            <p className="text-gray-700">
              <strong>Total:</strong> {order.total}
            </p>
            <p className={`mt-2 font-semibold ${order.status === 'Delivered' ? 'text-green-600' : 'text-yellow-600'}`}>
              {order.status}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default OrderHistory;
