const UserDashboard = () => {
  return (
    <div className="min-h-screen bg-gray-50 flex">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg">
        <div className="p-6 text-2xl font-bold text-gray-800 border-b">
          My Account
        </div>
        <nav className="flex flex-col p-4 space-y-2 text-gray-700">
          <NavLink
            to="orders"
            className={({ isActive }) =>
              `px-4 py-2 rounded hover:bg-gray-100 ${isActive ? 'bg-gray-200 font-semibold' : ''}`
            }
          >
            ðŸ§¾ Order History
          </NavLink>
          <NavLink
            to="wishlist"
            className={({ isActive }) =>
              `px-4 py-2 rounded hover:bg-gray-100 ${isActive ? 'bg-gray-200 font-semibold' : ''}`
            }
          >
            ðŸ’– Wishlist
          </NavLink>
          <NavLink
            to="account"
            className={({ isActive }) =>
              `px-4 py-2 rounded hover:bg-gray-100 ${isActive ? 'bg-gray-200 font-semibold' : ''}`
            }
          >
            ðŸ‘¤ Account Details
          </NavLink>
        </nav>
      </aside>

      {/* Content */}
      <main className="flex-1 p-8">
        <Outlet />
      </main>
    </div>
  );
};

export default UserDashboard;
