import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const HomePage = () => {
  const { dispatch } = useCart();

  const handleAddToCart = (productId) => {
    const product = {
      _id: productId,
      name: `Product Name ${productId}`,
      price: 1999,
      description: 'Brief description of the item.',
    };

    dispatch({ type: 'ADD_TO_CART', payload: product });
  };

  return (
    <div className="min-h-screen bg-white text-center py-10 px-4">
      {/* Hero Section */}
      <section className="mb-16">
        <h1 className="text-5xl font-bold mb-4 text-gray-900">
          Welcome to Luxor Clothing
        </h1>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto mb-8">
          Step into a world of elegance and comfort. At Luxor, we blend timeless fashion with modern flair.
        </p>
        <div className="flex flex-col sm:flex-row justify-center gap-4 mb-10">
          <Link to="/login" className="bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition">
            Login
          </Link>
          <Link to="/register" className="border border-black px-6 py-2 rounded hover:bg-gray-100 transition">
            Register
          </Link>
          <Link to="/products" className="bg-gray-700 text-white px-6 py-2 rounded hover:bg-gray-900 transition">
            View Products
          </Link>
        </div>
      </section>

      {/* Dashboard Links */}
      <section className="mb-16">
        <h2 className="text-2xl font-semibold mb-6">Your Dashboard</h2>
        <div className="flex flex-col sm:flex-row justify-center gap-6">
          <Link to="/userdashboard/orders" className="bg-slate-600 text-white px-6 py-3 rounded shadow hover:bg-indigo-700">
            üßæ My Orders
          </Link>
          <Link to="/userdashboard/wishlist" className="bg-slate-500 text-white px-6 py-3 rounded shadow hover:bg-pink-600">
            ‚ù§Ô∏è Wishlist
          </Link>
          <Link to="/userdashboard/account" className="bg-slate-500 text-white px-6 py-3 rounded shadow hover:bg-green-600">
            üë§ My Account
          </Link>
        </div>
      </section>

      {/* Product Preview Section */}
      <section>
        <h2 className="text-2xl font-semibold mb-6">Featured Styles</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 max-w-6xl mx-auto">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="bg-white shadow rounded overflow-hidden">
              <div className="h-60 bg-gray-200 flex items-center justify-center">
                <span className="text-gray-500 text-lg">Product {i}</span>
              </div>
              <div className="p-4 text-left">
                <h3 className="font-medium text-gray-800 mb-2">Product Name {i}</h3>
                <p className="text-sm text-gray-600 mb-2">Brief description of the item.</p>
                <span className="font-semibold text-black block mb-4">‚Çπ1,999</span>
                <button
                  onClick={() => handleAddToCart(i)}
                  className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition"
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
