import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';

const ProductDetailPage = () => {
  const { id } = useParams();
  const { addToCart } = useCart();
  const [product, setProduct] = useState(null);
  const [selectedSize, setSelectedSize] = useState('');
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/products/${id}`);
        setProduct(res.data);
      } catch (err) {
        console.error('Error fetching product:', err);
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (!selectedSize) {
      alert('Please select a size');
      return;
    }
    addToCart({ ...product, selectedSize, quantity });
    alert('Product added to cart!');
  };

  if (!product) {
    return <div className="p-10 text-center text-xl text-gray-700">Loading...</div>;
  }

  return (
    <div className="max-w-6xl mx-auto px-6 py-10 grid grid-cols-1 md:grid-cols-2 gap-10">
      <img src={product.image} alt={product.name} className="w-full object-cover rounded shadow" />
      <div className="space-y-6">
        <h1 className="text-3xl font-bold text-gray-900">{product.name}</h1>
        <p className="text-gray-600 text-sm">Category: {product.category}</p>
        <p className="text-xl text-black font-semibold">â‚¹{product.price.toLocaleString()}</p>

        <div>
          <h3 className="text-sm font-medium text-gray-700 mb-1">Select Size:</h3>
          <div className="flex gap-3">
            {product.sizes?.map((size) => (
              <button
                key={size}
                className={`px-4 py-1 border rounded hover:bg-gray-200 ${
                  selectedSize === size ? 'bg-black text-white' : 'text-gray-700'
                }`}
                onClick={() => setSelectedSize(size)}
              >
                {size}
              </button>
            ))}
          </div>
        </div>

        <div>
          <label htmlFor="quantity" className="text-sm text-gray-600 mr-2">Quantity:</label>
          <input
            id="quantity"
            type="number"
            min="1"
            value={quantity}
            onChange={(e) => setQuantity(parseInt(e.target.value))}
            className="w-20 border px-2 py-1 rounded"
          />
        </div>

        <button
          onClick={handleAddToCart}
          className="px-6 py-2 bg-black text-white rounded hover:bg-gray-800 transition"
        >
          Add to Cart
        </button>

        <p className="text-gray-700 text-sm leading-relaxed">
          {product.description || 'This is a premium fashion piece designed for style and comfort.'}
        </p>
      </div>
    </div>
  );
};

export default ProductDetailPage;
