// src/context/WishlistContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';
import axios from 'axios';

const WishlistContext = createContext();

const wishlistReducer = (state, action) => {
  switch (action.type) {
    case 'INIT_WISHLIST':
      return action.payload;
    case 'ADD_TO_WISHLIST':
      if (state.find(item => item._id === action.payload._id)) return state;
      return [...state, action.payload];
    case 'REMOVE_FROM_WISHLIST':
      return state.filter(item => item._id !== action.payload);
    case 'UPDATE_WISHLIST_ITEM':
      return state.map(item =>
        item._id === action.payload._id ? { ...item, ...action.payload } : item
      );
    default:
      return state;
  }
};

export const WishlistProvider = ({ children }) => {
  const [wishlistItems, dispatch] = useReducer(
    wishlistReducer,
    [],
    () => {
      const saved = localStorage.getItem('wishlist');
      return saved ? JSON.parse(saved) : [];
    }
  );

  // ✅ Persist to localStorage
  useEffect(() => {
    localStorage.setItem('wishlist', JSON.stringify(wishlistItems));
  }, [wishlistItems]);

  // ✅ Live sync stock every 30s
  useEffect(() => {
    const syncStock = async () => {
      try {
        const updatedItems = await Promise.all(
          wishlistItems.map(async (item) => {
            try {
              const res = await axios.get(`http://localhost:3001/api/products/${item._id}`);
              return { ...item, countInStock: res.data.countInStock, active: res.data.active };
            } catch {
              return { ...item, countInStock: 0, active: false }; // fallback
            }
          })
        );
        updatedItems.forEach((p) =>
          dispatch({ type: 'UPDATE_WISHLIST_ITEM', payload: p })
        );
      } catch (err) {
        console.error("❌ Error syncing wishlist stock:", err);
      }
    };

    if (wishlistItems.length > 0) {
      syncStock();
      const interval = setInterval(syncStock, 30000);
      return () => clearInterval(interval);
    }
  }, [wishlistItems]);

  const addToWishlist = (item) =>
    dispatch({ type: 'ADD_TO_WISHLIST', payload: item });

  const removeFromWishlist = (id) =>
    dispatch({ type: 'REMOVE_FROM_WISHLIST', payload: id });

  return (
    <WishlistContext.Provider value={{ wishlistItems, addToWishlist, removeFromWishlist }}>
      {children}
    </WishlistContext.Provider>
  );
};

export const useWishlist = () => useContext(WishlistContext);
