import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import toast from 'react-hot-toast';

const CheckoutPage = () => {
  const { cartItems, clearCart } = useCart();
  const navigate = useNavigate();

  const [loadingAddress, setLoadingAddress] = useState(true);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    postalCode: ''
  });

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/auth/me', { withCredentials: true });
        const user = res.data.user;

        setFormData((prev) => ({
          ...prev,
          name: user.name || '',
          email: user.email || '',
          phone: user.phone || '',
          address: user.address?.street || '',
          city: user.address?.city || '',
          postalCode: user.address?.postalCode || ''
        }));

        setLoadingAddress(false);
      } catch (err) {
        console.error('Error loading user info:', err);
        toast.error('Failed to load address info');
        setLoadingAddress(false);
      }
    };

    fetchUser();
  }, []);

  useEffect(() => {
    if (cartItems.length === 0) {
      navigate('/cart');
    }
  }, [cartItems, navigate]);

  const total = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handlePlaceOrder = async (e) => {
    e.preventDefault();
    if (cartItems.length === 0) {
      toast.error('Your cart is empty');
      return;
    }

    const hasMissingSize = cartItems.some(item => !item.size);
    if (hasMissingSize) {
      toast.error('Please select size for all items before placing order');
      return;
    }

    try {
      // Save address if not already saved
      await axios.post(
        'http://localhost:3001/api/auth/update-address',
        {
          phone: formData.phone,
          address: {
            street: formData.address,
            city: formData.city,
            postalCode: formData.postalCode,
          },
        },
        { withCredentials: true }
      );

      // Format cartItems to include required fields
      const orderItems = cartItems.map(item => ({
        product: item._id || item.id,
        name: item.name,
        qty: item.quantity,
        price: item.price,
        image: item.image
      }));

      console.log('Sending orderItems:', orderItems);

      // Place order to backend
      const response = await axios.post(
        'http://localhost:3001/api/orders',
        {
          orderItems,
          totalPrice: total,
          paymentMethod: 'Cash on Delivery',
          shippingAddress: {
            address: formData.address,
            city: formData.city,
            postalCode: formData.postalCode,
            country: 'India',
            phone: formData.phone
          }
        },
        { withCredentials: true }
      );

      if (response.status === 201 || response.status === 200) {
        toast.success('Order placed successfully!');
        clearCart();
        navigate('/order-success');
      }
    } catch (err) {
      if (err.response) {
        console.error('Error placing order:', err.response.data);
        toast.error(err.response.data.message || 'Failed to place order');
      } else {
        console.error('Error placing order:', err);
        toast.error('Failed to place order');
      }
    }
  };

  if (loadingAddress) return <div className="text-center py-20">Loading address...</div>;

  return (
    <div className="max-w-5xl mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold mb-8">Checkout</h1>

      <form onSubmit={handlePlaceOrder}>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-10">
          <input name="name" value={formData.name} onChange={handleChange} placeholder="Full Name" className="border p-2 rounded" required />
          <input name="email" value={formData.email} onChange={handleChange} placeholder="Email" className="border p-2 rounded" required />
          <input name="phone" value={formData.phone} onChange={handleChange} placeholder="Phone Number" className="border p-2 rounded" required />
          <input name="address" value={formData.address} onChange={handleChange} placeholder="Address" className="border p-2 rounded" required />
          <input name="city" value={formData.city} onChange={handleChange} placeholder="City" className="border p-2 rounded" required />
          <input name="postalCode" value={formData.postalCode} onChange={handleChange} placeholder="Postal Code" className="border p-2 rounded" required />
        </div>

        <div className="bg-white shadow p-6 rounded mb-6">
          <h2 className="text-xl font-semibold mb-4">Order Summary</h2>
          {cartItems.map((item) => (
            <div key={item._id || item.id} className="flex justify-between mb-2">
              <span>{item.name} × {item.quantity}</span>
              <span>₹{item.price * item.quantity}</span>
            </div>
          ))}
          <hr className="my-4" />
          <div className="flex justify-between font-bold text-lg">
            <span>Total:</span>
            <span>₹{total.toLocaleString()}</span>
          </div>
        </div>

        <button
          type="submit"
          className="bg-black text-white px-6 py-3 rounded hover:bg-gray-800"
        >
          Place Order
        </button>
      </form>
    </div>
  );
};

export default CheckoutPage;
