// src/context/CartContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';

// Initial state
const initialState = {
  cartItems: [],
};

// Reducer
const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const exists = state.cartItems.find(
        item => item._id === action.payload._id && item.size === action.payload.size
      );
      if (exists) {
        return {
          ...state,
          cartItems: state.cartItems.map(item =>
            item._id === action.payload._id && item.size === action.payload.size
              ? { ...item, quantity: item.quantity + 1 }
              : item
          ),
        };
      }
      return {
        ...state,
        cartItems: [...state.cartItems, { ...action.payload, quantity: 1 }],
      };

    case 'REMOVE_FROM_CART':
      return {
        ...state,
        cartItems: state.cartItems.filter(
          item => !(item._id === action.payload._id && item.size === action.payload.size)
        ),
      };

    case 'INCREASE_QUANTITY':
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item._id === action.payload._id && item.size === action.payload.size
            ? { ...item, quantity: item.quantity + 1 }
            : item
        ),
      };

    case 'DECREASE_QUANTITY':
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item._id === action.payload._id && item.size === action.payload.size && item.quantity > 1
            ? { ...item, quantity: item.quantity - 1 }
            : item
        ),
      };

    case 'CLEAR_CART':
      return {
        ...state,
        cartItems: [],
      };

    case 'UPDATE_SIZE':
      return {
        ...state,
        cartItems: state.cartItems.map(item =>
          item._id === action.payload.id
            ? { ...item, size: action.payload.size }
            : item
        ),
      };

    default: {
      return state;
    }
  }
};

// Context and Provider
const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(
    cartReducer,
    { cartItems: JSON.parse(localStorage.getItem('cartItems')) || [] }
  );

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(state.cartItems));
  }, [state.cartItems]);

  console.log("ðŸ“¦ CartProvider initialized â€” dispatch type:", typeof dispatch);

  const addToCart = (product) => {
    const size = product.size || product.selectedSize;
    if (!product._id || !size) {
      console.warn("ðŸš« Cannot add to cart: Missing _id or size.");
      return;
    }

    console.log("ðŸ›’ Product being added to cart:", product); // debug log
    dispatch({ type: 'ADD_TO_CART', payload: { ...product, size } });
  };

  const removeFromCart = (_id, size) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: { _id, size } });
  };

  const increaseQuantity = (_id, size) => {
    dispatch({ type: 'INCREASE_QUANTITY', payload: { _id, size } });
  };

  const decreaseQuantity = (_id, size) => {
    dispatch({ type: 'DECREASE_QUANTITY', payload: { _id, size } });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  const updateSize = (id, size) => {
    dispatch({ type: 'UPDATE_SIZE', payload: { id, size } });
  };

  return (
    <CartContext.Provider
  value={{
    state,
    dispatch,
    cartItems: state.cartItems,
    addToCart,
    removeFromCart,
    increaseQuantity,
    decreaseQuantity,
    clearCart,
    updateSize,
  }}
>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
