import React, { useState, useEffect } from 'react';
import { useWishlist } from '../context/WishlistContext';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';
import toast from 'react-hot-toast';
import axios from 'axios';

const Wishlist = () => {
  const { wishlistItems, removeFromWishlist } = useWishlist();
  const { addToCart } = useCart();
  const [selectedSizes, setSelectedSizes] = useState({});

  // ‚úÖ Engagement notification after 10s
  useEffect(() => {
    const timer = setTimeout(() => {
      if (wishlistItems.length > 0) {
        toast("Others Are Eyeing Your Picks‚ÄîSnag ‚ÄòEm Before They‚Äôre Gone!", {
          icon: "ü´†",
        });
      }
    }, 10000);

    return () => clearTimeout(timer);
  }, [wishlistItems]);

  // ‚úÖ Check live stock before moving to cart
  const handleMoveToCart = async (item) => {
    const selectedSize = selectedSizes[item._id];
    if (!selectedSize) {
      toast.error(`Please select a size for "${item.name}"`);
      return;
    }

    try {
      const res = await axios.get(`http://localhost:3001/api/products/${item._id}`);
      const liveProduct = res.data;

      if (!liveProduct.active || liveProduct.countInStock <= 0) {
        toast.error(`"${item.name}" is currently out of stock`);
        return;
      }

      addToCart({ ...item, size: selectedSize, countInStock: liveProduct.countInStock });
      removeFromWishlist(item._id);
      toast.success(`Moved "${item.name}" to cart`);
    } catch (err) {
      console.error("‚ùå Stock check failed:", err);
      toast.error("Could not verify stock, please try again");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">‚ù§Ô∏è My Wishlist</h1>

      {wishlistItems.length === 0 ? (
        <div className="text-center text-gray-500">
          Your wishlist is empty.
          <br />
          <Link to="/products" className="text-blue-600 hover:underline">Browse products</Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {wishlistItems.map(item => (
            <div key={item._id} className="bg-white shadow rounded p-4">
              <img src={item.image} alt={item.name} className="w-full h-60 object-cover rounded mb-4" />
              <h2 className="text-lg font-semibold text-gray-800 mb-1">{item.name}</h2>
              <p className="text-sm text-gray-600 mb-2">‚Çπ{item.price}</p>

              {/* ‚úÖ Size selection */}
              <select
                value={selectedSizes[item._id] || ''}
                onChange={(e) =>
                  setSelectedSizes((prev) => ({
                    ...prev,
                    [item._id]: e.target.value,
                  }))
                }
                className="w-full mb-3 px-3 py-2 border rounded text-gray-700"
              >
                <option value="">Select Size</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>

              <div className="flex justify-between items-center">
                <Link to={`/product/${item._id}`} className="text-blue-600 hover:underline">
                  View
                </Link>

                <button
                  onClick={() => handleMoveToCart(item)}
                  className="px-3 py-1 bg-black text-white rounded hover:bg-gray-800 transition"
                >
                  Move to Cart
                </button>

                <button
                  onClick={() => removeFromWishlist(item._id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
