import React, { useEffect, useState, useMemo } from 'react';
import axios from 'axios';
import toast, { Toaster } from 'react-hot-toast';

const initialProductForm = {
  name: '',
  brand: '',
  category: '',
  description: '',
  price: '',
  countInStock: '',
  sizes: '',
  colors: '',
  images: [],
  imagesByColor: {},   // âœ… map { color: [urls] }
  isFeatured: false,
  active: true,
};

export default function AdminProductsPage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('');
  const [filterActive, setFilterActive] = useState('');
  const [selectedProducts, setSelectedProducts] = useState(new Set());
  const [page, setPage] = useState(1);
  const [limit] = useState(10);
  const [totalPages, setTotalPages] = useState(1);

  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [productForm, setProductForm] = useState(initialProductForm);
  const [uploadingImage, setUploadingImage] = useState(false);
  const [manualImageUrl, setManualImageUrl] = useState('');


  // âœ… Track selected file globally
  const [selectedFile, setSelectedFile] = useState(null);

  // Fetch products with filters, search, pagination
  const fetchProducts = async () => {
    setLoading(true);
    try {
      const params = {
        _page: page,
        _limit: limit,
      };
      if (searchTerm.trim()) params.q = searchTerm.trim();
      if (filterCategory) params.category = filterCategory;
      if (filterActive) params.active = filterActive === 'true';
      const { data, headers } = await axios.get('http://localhost:3001/api/products', {
        params,
        withCredentials: true,
      });
      setProducts(data);
      // Calculate total pages from headers if available
      const totalCount = headers['x-total-count'] ? parseInt(headers['x-total-count'], 10) : data.length;
      setTotalPages(Math.ceil(totalCount / limit));
    } catch (error) {
      toast.error('Failed to fetch products');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [page, searchTerm, filterCategory, filterActive]);

  // Handle checkbox toggle for single product
  const toggleProductSelection = (id) => {
    setSelectedProducts((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  // Handle toggle all checkbox
  const toggleSelectAll = () => {
    if (selectedProducts.size === products.length) {
      setSelectedProducts(new Set());
    } else {
      setSelectedProducts(new Set(products.map((p) => p._id)));
    }
  };

  // Validate product form inputs
  const validateProductForm = () => {
    if (!productForm.name.trim()) {
      toast.error('Name is required');
      return false;
    }
    if (!productForm.brand.trim()) {
      toast.error('Brand is required');
      return false;
    }
    if (!productForm.category.trim()) {
      toast.error('Category is required');
      return false;
    }
    if (!productForm.price || isNaN(productForm.price) || Number(productForm.price) < 0) {
      toast.error('Price must be a non-negative number');
      return false;
    }
    if (
      productForm.countInStock === '' ||
      isNaN(productForm.countInStock) ||
      Number(productForm.countInStock) < 0
    ) {
      toast.error('Count In Stock must be a non-negative number');
      return false;
    }
    return true;
  };

  // Handle Add Product modal open
  const openAddModal = () => {
    setProductForm(initialProductForm);
    setManualImageUrl('');
    setShowAddModal(true);
  };

  // Handle Edit Product modal open
  const openEditModal = (product) => {
    setEditingProduct(product);
    setProductForm({
      name: product.name || '',
      brand: product.brand || '',
      category: product.category || '',
      description: product.description || '',
      price: product.price || '',
      countInStock: product.countInStock || '',
      sizes: (product.sizes || []).join(','),
      colors: (product.colors || []).join(','),
      imagesByColor: product.imagesByColor || {},
      images: product.images || [],
      isFeatured: !!product.isFeatured,
      variants: product.variants || [],
      active: product.active === undefined ? true : product.active,
    });
    setManualImageUrl('');
    setShowEditModal(true);
  };

  // âœ… Handle image upload (just select, upload on save)
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setSelectedFile(file); // âœ… save file for later upload
    toast.success("Image selected, will upload on save");
    e.target.value = null;
  };

  // Add manual image URL to images array
  const addManualImageUrl = () => {
    if (!manualImageUrl.trim()) {
      toast.error('Image URL cannot be empty');
      return;
    }
    setProductForm((prev) => ({
      ...prev,
      images: [...prev.images, manualImageUrl.trim()],
    }));
    setManualImageUrl('');
  };

  // Remove image from images array by index
  const removeImageAtIndex = (idx) => {
    setProductForm((prev) => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== idx),
    }));
  };

  // âœ… Handle create product
const handleCreate = async () => {
  if (!validateProductForm()) return;

  try {
    if (selectedFile) {
      // --- CASE 1: File selected â†’ use FormData ---
      const formData = new FormData();
      formData.append("image", selectedFile); // âœ… main file

      // append other text fields
      formData.append("name", productForm.name);
      formData.append("brand", productForm.brand);
      formData.append("category", productForm.category);
      formData.append("description", productForm.description);
      formData.append("price", Number(productForm.price));
      formData.append("countInStock", Number(productForm.countInStock));
      formData.append("isFeatured", productForm.isFeatured);
      formData.append("active", productForm.active);

      // append sizes
      productForm.sizes
        .split(",")
        .map((s) => s.trim())
        .filter(Boolean)
        .forEach((s) => formData.append("sizes", s));

      // append colors
      productForm.colors
        .split(",")
        .map((c) => c.trim())
        .filter(Boolean)
        .forEach((c) => formData.append("colors", c));

      // append manual image URLs
      productForm.images.forEach((url) => formData.append("images", url));

      // Instead of appending each variant separately
// Collect them into one array
const cleanVariants = (productForm.variants || []).map((v) => ({
  size: v.size,
  color: v.color,
  stock: Number(v.stock) || 0,
}));

formData.append("variants", JSON.stringify(cleanVariants));

      console.log("ðŸ“¤ Sending FormData (with file):");
      for (let pair of formData.entries()) {
        console.log(pair[0], pair[1]);
      }

      await axios.post("http://localhost:3001/api/products", formData, {
        withCredentials: true,
        headers: { "Content-Type": "multipart/form-data" },
      });
    } else {
      // --- CASE 2: Only URLs â†’ send JSON ---
      const payload = {
        ...productForm,
        price: Number(productForm.price),
        countInStock: Number(productForm.countInStock),
        sizes: productForm.sizes
          ? productForm.sizes.split(",").map((s) => s.trim()).filter(Boolean)
          : [],
        colors: productForm.colors
          ? productForm.colors.split(",").map((c) => c.trim()).filter(Boolean)
          : [],
        images: productForm.images,
        variants: (productForm.variants || []).map(v => ({
  size: v.size,
  color: v.color,
  stock: Number(v.stock) || 0
})),
      };

      console.log("ðŸ“¤ Sending JSON payload:", payload);

      await axios.post("http://localhost:3001/api/products", payload, {
        withCredentials: true,
        headers: { "Content-Type": "application/json" },
      });
    }

    toast.success("Product created âœ…");
    setShowAddModal(false);
    setSelectedFile(null);
    fetchProducts();
  } catch (err) {
  if (err.response) {
    console.error("âŒ Create product error:", err.response.data);
    toast.error(err.response.data.message || "Failed to create product");
  } else {
    console.error("âŒ Ceate product error:", err);
    toast.error("Failed to create product");
  }
}
};

// âœ… Handle update product
const handleUpdate = async () => {
  if (!validateProductForm()) return;

  try {
    if (selectedFile) {
      // --- CASE 1: File selected â†’ use FormData ---
      const formData = new FormData();
      formData.append("image", selectedFile);

      formData.append("name", productForm.name);
      formData.append("brand", productForm.brand);
      formData.append("category", productForm.category);
      formData.append("description", productForm.description);
      formData.append("price", Number(productForm.price));
      formData.append("countInStock", Number(productForm.countInStock));
      formData.append("isFeatured", productForm.isFeatured);
      formData.append("active", productForm.active);

      productForm.sizes
        .split(",")
        .map((s) => s.trim())
        .filter(Boolean)
        .forEach((s) => formData.append("sizes", s));

      productForm.colors
        .split(",")
        .map((c) => c.trim())
        .filter(Boolean)
        .forEach((c) => formData.append("colors", c));

      productForm.images.forEach((url) => formData.append("images", url));

      // Instead of appending each variant separately
// Collect them into one array
const cleanVariants = (productForm.variants || []).map((v) => ({
  size: v.size,
  color: v.color,
  stock: Number(v.stock) || 0,
}));

formData.append("variants", JSON.stringify(cleanVariants));

      console.log("ðŸ“¤ Updating with FormData (with file):");
      for (let pair of formData.entries()) {
        console.log(pair[0], pair[1]);
      }

      await axios.put(
        `http://localhost:3001/api/products/${editingProduct._id}`,
        formData,
        {
          withCredentials: true,
          headers: { "Content-Type": "multipart/form-data" },
        }
      );
    } else {
      // --- CASE 2: Only URLs â†’ send JSON ---
      const payload = {
        ...productForm,
        price: Number(productForm.price),
        countInStock: Number(productForm.countInStock),
        sizes: productForm.sizes
          ? productForm.sizes.split(",").map((s) => s.trim()).filter(Boolean)
          : [],
        colors: productForm.colors
          ? productForm.colors.split(",").map((c) => c.trim()).filter(Boolean)
          : [],
        images: productForm.images,
        variants: (productForm.variants || []).map(v => ({
  size: v.size,
  color: v.color,
  stock: Number(v.stock) || 0
})),
      };

      console.log("ðŸ“¤ Updating with JSON payload:", payload);

      await axios.put(
        `http://localhost:3001/api/products/${editingProduct._id}`,
        payload,
        {
          withCredentials: true,
          headers: { "Content-Type": "application/json" },
        }
      );
    }

    toast.success("Product updated âœ…");
    setShowEditModal(false);
    setEditingProduct(null);
    setSelectedFile(null);
    fetchProducts();
  } catch (err) {
  if (err.response) {
    console.error("âŒ Update product error:", err.response.data);
    toast.error(err.response.data.message || "Failed to update product");
  } else {
    console.error("âŒ Update product error:", err);
    toast.error("Failed to update product");
  }
}
};
  // Handle delete product
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await axios.delete(`http://localhost:3001/api/products/${id}`, {
  withCredentials: true,
});
      toast.success('Product deleted');
      setSelectedProducts((prev) => {
        const newSet = new Set(prev);
        newSet.delete(id);
        return newSet;
      });
      fetchProducts();
    } catch {
      toast.error('Failed to delete product');
    }
  };

  // Handle bulk delete
  const handleBulkDelete = async () => {
  if (selectedProducts.size === 0) {
    toast.error('No products selected');
    return;
  }

  if (!window.confirm(`Are you sure you want to delete ${selectedProducts.size} products?`)) return;

  try {
    await axios.delete("http://localhost:3001/api/products", {
      data: { ids: Array.from(selectedProducts) }, // send array of IDs
      withCredentials: true,
    });

    toast.success('Selected products deleted');
    setSelectedProducts(new Set()); // clear selection
    fetchProducts(); // refresh product list
  } catch (err) {
    console.error("âŒ Bulk delete error:", err);
    toast.error('Failed to delete selected products');
  }
};


  // Handle toggle active status for a product
  const handleToggleActive = async (product) => {
  try {
    const newStatus = !product.active; // flip the status

    await axios.put(
      `http://localhost:3001/api/products/${product._id}`,
      { active: newStatus }, // only send the active field
      { withCredentials: true }
    );

    toast.success(`Product ${newStatus ? "activated" : "deactivated"} âœ…`);
    fetchProducts(); // refresh product list after toggle
  } catch (err) {
    console.error("âŒ Toggle active error:", err.response?.data || err.message);
    toast.error("Failed to toggle active status");
  }
};

  // Export displayed products to CSV
const handleExportCSV = () => {
  if (products.length === 0) {
    toast.error('No products to export');
    return;
  }

  // âœ… Category normalizer (Zara style)
  const normalizeCategory = (cat) => {
    if (!cat) return "";
    const c = cat.toLowerCase();
    if (c.includes("tshirt") || c.includes("tee")) return "T-Shirt";
    if (c.includes("shirt")) return "Shirt";
    if (c.includes("jean")) return "Jeans";
    if (c.includes("hood")) return "Hoodie";
    if (c.includes("pant") || c.includes("trouser")) return "Trousers";
    return cat; // fallback: keep original
  };

  const headers = [
    'ID',
    'Name',
    'Brand',
    'Category',
    'Description',
    'Price',
    'CountInStock',
    'Sizes',
    'Colors',
    'Images',
    'IsFeatured',
    'Active',
  ];

  const rows = products.map((p) => [
    p._id,
    p.name,
    p.brand,
    normalizeCategory(p.category), // âœ… Normalized here
    p.description ? p.description.replace(/(\r\n|\n|\r)/gm, ' ') : '',
    `â‚¹${p.price}`, // âœ… Rupees
    p.countInStock,
    (p.sizes || []).join('|'),
    (p.colors || []).join('|'),
    (p.images || []).join('|'),
    p.isFeatured ? 'Yes' : 'No',
    p.active ? 'Yes' : 'No',
  ]);

  const csvContent =
    'data:text/csv;charset=utf-8,' +
    [headers, ...rows]
      .map((e) =>
        e.map((v) => `"${String(v).replace(/"/g, '""')}"`).join(','),
      )
      .join('\n');

  const encodedUri = encodeURI(csvContent);
  const link = document.createElement('a');
  link.setAttribute('href', encodedUri);
  link.setAttribute('download', `products_export_${Date.now()}.csv`);
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
};

  // Pagination controls
  const canPrev = page > 1;
  const canNext = page < totalPages;

  return (
