    <button
      type="button"
      className="px-3 py-2 bg-black text-white rounded"
      onClick={() => {
        if (!galleryColor.trim() || !galleryUrl.trim()) return;
        setProductForm((f) => {
          const map = { ...(f.imagesByColor || {}) };
          const key = galleryColor.trim();
          map[key] = [...(map[key] || []), galleryUrl.trim()];
          return { ...f, imagesByColor: map };
        });
        setGalleryUrl("");
      }}
    >
      + Add
    </button>
