// src/admin/AdminOrdersPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import toast from "react-hot-toast";

const AdminOrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [search, setSearch] = useState("");
  const [statusFilter, setStatusFilter] = useState("All");
  const [isAdmin, setIsAdmin] = useState(false);

  // Modal state
  const [selectedOrder, setSelectedOrder] = useState(null);

  // Confirmation modal state for orders
  const [confirmModal, setConfirmModal] = useState({
    visible: false,
    orderId: null,
    newStatus: "",
  });

  // New state variables for product management
  const [activeTab, setActiveTab] = useState("orders"); // 'orders' or 'products'
  const [products, setProducts] = useState([]);
  const [loadingProducts, setLoadingProducts] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [confirmDeleteProduct, setConfirmDeleteProduct] = useState({
    visible: false,
    productId: null,
  });

  useEffect(() => {
    checkAdmin();
  }, []);

  const checkAdmin = async () => {
    try {
      const res = await axios.get("http://localhost:3001/api/auth/me", {
        withCredentials: true,
      });
      if (res.data?.user?.role === "admin") {
        setIsAdmin(true);
        fetchOrders();
      } else {
        toast.error("Access denied. Admins only.");
        window.location.href = "/";
      }
    } catch (err) {
      console.error(err);
      toast.error("Unauthorized. Please login as admin.");
      window.location.href = "/login";
    }
  };

  const fetchOrders = async () => {
    try {
      const res = await axios.get("http://localhost:3001/api/orders", {
        withCredentials: true,
      });
      setOrders(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      toast.error("Failed to load orders");
      setLoading(false);
    }
  };

  // New function to fetch products
  const fetchProducts = async () => {
    setLoadingProducts(true);
    try {
      const res = await axios.get("http://localhost:3001/api/products", {
        withCredentials: true,
      });
      setProducts(res.data);
    } catch (err) {
      console.error(err);
      toast.error("Failed to load products");
    } finally {
      setLoadingProducts(false);
    }
  };

  // Remove direct call to axios and window.confirm from here
  const handleStatusChange = (orderId, newStatus) => {
    setConfirmModal({ visible: true, orderId, newStatus });
  };

  const confirmStatusChange = async () => {
    const { orderId, newStatus } = confirmModal;
    try {
      await axios.put(
        `http://localhost:3001/api/orders/${orderId}/status`,
        { status: newStatus },
        { withCredentials: true }
      );
      toast.success("Order status updated");
      await fetchOrders(); // refresh orders

      // Refresh selectedOrder to reflect updated status in modal
      const updatedOrder = orders.find((order) => order._id === orderId);
      if (updatedOrder) {
        setSelectedOrder({ ...updatedOrder, status: newStatus });
      } else {
        // In case order not found in current state, refetch orders and update modal
        const res = await axios.get(`http://localhost:3001/api/orders/${orderId}`, {
          withCredentials: true,
        });
        setSelectedOrder(res.data);
      }
    } catch (err) {
      console.error(err);
      toast.error("Failed to update status");
    } finally {
      setConfirmModal({ visible: false, orderId: null, newStatus: "" });
    }
  };

  // âœ… Filtering orders
  const filteredOrders = orders.filter((order) => {
    const matchesSearch =
      order._id.toLowerCase().includes(search.toLowerCase()) ||
      order.user?.email?.toLowerCase().includes(search.toLowerCase());

    const matchesStatus =
      statusFilter === "All" || order.status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  // Products handlers

  const handleToggleActive = async (productId, currentActive) => {
    try {
      await axios.put(
        `http://localhost:3001/api/products/${productId}/active`,
        { isActive: !currentActive },
        { withCredentials: true }
      );
      toast.success(`Product ${!currentActive ? "activated" : "deactivated"}`);
      fetchProducts();
      if (selectedProduct && selectedProduct._id === productId) {
        setSelectedProduct((prev) => ({ ...prev, isActive: !currentActive }));
      }
    } catch (err) {
      console.error(err);
      toast.error("Failed to update product active status");
    }
  };

  const handleSaveProduct = async (updatedProduct) => {
    try {
      await axios.put(
        `http://localhost:3001/api/products/${updatedProduct._id}`,
        updatedProduct,
        { withCredentials: true }
      );
      toast.success("Product updated");
      fetchProducts();
      setSelectedProduct(null);
    } catch (err) {
      console.error(err);
      toast.error("Failed to update product");
    }
  };

  const handleDeleteProduct = async () => {
    const { productId } = confirmDeleteProduct;
    try {
      await axios.delete(
        `http://localhost:3001/api/products/${productId}`,
        { withCredentials: true }
      );
      toast.success("Product deleted");
      fetchProducts();
      setConfirmDeleteProduct({ visible: false, productId: null });
      if (selectedProduct && selectedProduct._id === productId) {
        setSelectedProduct(null);
      }
    } catch (err) {
      console.error(err);
      toast.error("Failed to delete product");
    }
  };

  // When switching to products tab, fetch products if not loaded
  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === "products" && products.length === 0) {
      fetchProducts();
    }
  };

  if (!isAdmin) return null;
  if (loading && activeTab === "orders") return <div className="p-10 text-center">Loading orders...</div>;
  if (loadingProducts && activeTab === "products") return <div className="p-10 text-center">Loading products...</div>;

  return (
    <div className="max-w-7xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6">Admin Panel</h1>

      {/* Tabs */}
      <div className="flex space-x-4 mb-6 border-b border-gray-300">
        <button
          className={`pb-2 font-semibold ${activeTab === "orders" ? "border-b-2 border-black" : "text-gray-500"}`}
          onClick={() => handleTabChange("orders")}
        >
          Orders
        </button>
        <button
          className={`pb-2 font-semibold ${activeTab === "products" ? "border-b-2 border-black" : "text-gray-500"}`}
          onClick={() => handleTabChange("products")}
        >
          Products
        </button>
      </div>

      {activeTab === "orders" && (
        <>
          {/* âœ… Search & Filters */}
          <div className="flex flex-wrap items-center gap-4 mb-6">
            <input
              type="text"
              placeholder="Search by Order ID or Email"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border px-4 py-2 rounded w-72"
            />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="border px-4 py-2 rounded"
            >
              <option value="All">All Status</option>
              <option value="Processing">Processing</option>
              <option value="Packed">Packed</option>
              <option value="Shipped">Shipped</option>
              <option value="Out for Delivery">Out for Delivery</option>
              <option value="Delivered">Delivered</option>
            </select>
          </div>

          {/* âœ… Orders Table */}
          <div className="overflow-x-auto bg-white rounded shadow">
            <table className="min-w-full text-sm text-left">
              <thead className="bg-gray-200 text-gray-700 uppercase">
                <tr>
                  <th className="px-4 py-3">Order ID</th>
                  <th className="px-4 py-3">User</th>
                  <th className="px-4 py-3">Items</th>
                  <th className="px-4 py-3">Total</th>
                  <th className="px-4 py-3">Status</th>
                  <th className="px-4 py-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {filteredOrders.length > 0 ? (
                  filteredOrders.map((order) => (
                    <tr key={order._id} className="border-t">
                      <td className="px-4 py-3">{order._id}</td>
                      <td className="px-4 py-3">{order.user?.email}</td>
                      <td className="px-4 py-3">
                        {order.orderItems.map((item, i) => (
                          <div key={i}>
                            {item.name} Ã— {item.qty}
                          </div>
                        ))}
                      </td>
                      <td className="px-4 py-3 font-semibold">â‚¹{order.totalPrice}</td>
                      <td className="px-4 py-3">
                        <span
                          className={`px-2 py-1 rounded text-xs font-medium ${
                            order.status === "Delivered"
                              ? "bg-green-200 text-green-800"
                              : order.status === "Shipped"
                              ? "bg-blue-200 text-blue-800"
                              : "bg-yellow-200 text-yellow-800"
                          }`}
                        >
                          {order.status}
                        </span>
                      </td>
                      <td className="px-4 py-3 flex gap-2">
                        <select
                          value={order.status}
                          onChange={(e) =>
                            handleStatusChange(order._id, e.target.value)
                          }
                          className="border rounded px-2 py-1"
                        >
                          <option value="Processing">Processing</option>
                          <option value="Packed">Packed</option>
                          <option value="Shipped">Shipped</option>
                          <option value="Out for Delivery">Out for Delivery</option>
                          <option value="Delivered">Delivered</option>
                        </select>
                        <button
                          onClick={() => setSelectedOrder(order)}
                          className="bg-black text-white px-3 py-1 rounded hover:bg-gray-800"
                        >
                          View Details
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="text-center py-6 text-gray-500">
                      No orders found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>

          {/* âœ… Order Details Modal */}
          {selectedOrder && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg shadow-lg max-w-2xl w-full">
                <h2 className="text-xl font-bold mb-4">Order Details</h2>
                <p><strong>Order ID:</strong> {selectedOrder._id}</p>
                <p><strong>User:</strong> {selectedOrder.user?.email}</p>
                <p><strong>Status:</strong> {selectedOrder.status}</p>
                <div className="flex items-center justify-between mt-4 mb-6">
                  {["Processing", "Packed", "Shipped", "Out for Delivery", "Delivered"].map((step, index) => {
                    const currentIndex = ["Processing", "Packed", "Shipped", "Out for Delivery", "Delivered"].indexOf(selectedOrder.status);
                    const isCompleted = currentIndex >= index;
                    return (
                      <div key={step} className="flex-1 flex flex-col items-center cursor-pointer"
                           onClick={() => handleStatusChange(selectedOrder._id, step)}>
                        <div
                          className={`w-8 h-8 rounded-full flex items-center justify-center text-xs font-bold transition ${
                            isCompleted ? "bg-green-600 text-white" : "bg-gray-300 text-gray-600 hover:bg-gray-400"
                          }`}
                        >
                          {index + 1}
                        </div>
                        <span className="text-xs mt-2">{step}</span>
                        {index < 4 && (
                          <div
                            className={`h-1 w-full ${
                              currentIndex > index ? "bg-green-600" : "bg-gray-300"
                            }`}
                          />
                        )}
                      </div>
                    );
                  })}
                </div>
                <div className="mt-4">
                  <h3 className="font-semibold mb-2">Items:</h3>
                  {selectedOrder.orderItems.map((item, i) => (
                    <div key={i} className="border-b py-2">
                      {item.name} Ã— {item.qty} â€“ â‚¹{item.price}
                    </div>
                  ))}
                </div>
                <div className="mt-4">
                  <h3 className="font-semibold mb-2">Shipping Address:</h3>
                  <p>{selectedOrder.shippingAddress?.address}, {selectedOrder.shippingAddress?.city}, {selectedOrder.shippingAddress?.postalCode}</p>
                  <p><strong>Phone:</strong> {selectedOrder.shippingAddress?.phone}</p>
                </div>
                <div className="mt-6 flex justify-end">
                  <button
                    onClick={() => setSelectedOrder(null)}
                    className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
                  >
                    Close
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Confirmation Modal for Order Status Change */}
          {confirmModal.visible && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
                <h2 className="text-lg font-bold mb-4">Confirm Status Change</h2>
                <p className="mb-6">
                  Are you sure you want to update the order status to <strong>{confirmModal.newStatus}</strong>?
                </p>
                <div className="flex justify-end gap-4">
                  <button
                    onClick={() => setConfirmModal({ visible: false, orderId: null, newStatus: "" })}
                    className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={confirmStatusChange}
                    className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
                  >
                    Confirm
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}

      {activeTab === "products" && (
        <>
          {/* Products Table */}
          <div className="overflow-x-auto bg-white rounded shadow">
            <table className="min-w-full text-sm text-left">
              <thead className="bg-gray-200 text-gray-700 uppercase">
                <tr>
                  <th className="px-4 py-3">ID</th>
                  <th className="px-4 py-3">Name</th>
                  <th className="px-4 py-3">Price</th>
                  <th className="px-4 py-3">Active</th>
                  <th className="px-4 py-3">Sizes</th>
                  <th className="px-4 py-3">Colors</th>
                  <th className="px-4 py-3">Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.length > 0 ? (
                  products.map((product) => (
                    <tr key={product._id} className="border-t">
                      <td className="px-4 py-3">{product._id}</td>
                      <td className="px-4 py-3">{product.name}</td>
                      <td className="px-4 py-3 font-semibold">â‚¹{product.price}</td>
                      <td className="px-4 py-3">
                        <button
                          onClick={() => handleToggleActive(product._id, product.isActive)}
                          className={`px-3 py-1 rounded text-white text-xs font-semibold ${
                            product.isActive ? "bg-green-600 hover:bg-green-700" : "bg-red-600 hover:bg-red-700"
                          }`}
                        >
                          {product.isActive ? "Active" : "Inactive"}
                        </button>
                      </td>
                      <td className="px-4 py-3">{product.sizes?.join(", ")}</td>
                      <td className="px-4 py-3">{product.colors?.join(", ")}</td>
                      <td className="px-4 py-3 flex gap-2">
                        <button
                          onClick={() => setSelectedProduct(product)}
                          className="bg-blue-600 text-white px-3 py-1 rounded hover:bg-blue-700 text-xs"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => setConfirmDeleteProduct({ visible: true, productId: product._id })}
                          className="bg-red-600 text-white px-3 py-1 rounded hover:bg-red-700 text-xs"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="7" className="text-center py-6 text-gray-500">
                      No products found.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>

          {/* Edit Product Modal */}
          {selectedProduct && (
            <EditProductModal
              product={selectedProduct}
              onClose={() => setSelectedProduct(null)}
              onSave={handleSaveProduct}
            />
          )}

          {/* Confirmation Modal for Delete Product */}
          {confirmDeleteProduct.visible && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
                <h2 className="text-lg font-bold mb-4">Confirm Delete Product</h2>
                <p className="mb-6">
                  Are you sure you want to delete this product? This action cannot be undone.
                </p>
                <div className="flex justify-end gap-4">
                  <button
                    onClick={() => setConfirmDeleteProduct({ visible: false, productId: null })}
                    className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleDeleteProduct}
                    className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                  >
                    Delete
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

// Component for editing product details
const EditProductModal = ({ product, onClose, onSave }) => {
  const [formData, setFormData] = useState({
    name: product.name || "",
    price: product.price || 0,
    sizes: product.sizes ? [...product.sizes] : [],
    colors: product.colors ? [...product.colors] : [],
  });

  // Helper to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "price") {
      setFormData((prev) => ({ ...prev, [name]: Number(value) }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  // Sizes and colors input handlers
  const handleSizesChange = (e) => {
    const val = e.target.value;
    const arr = val.split(",").map((s) => s.trim()).filter(s => s.length > 0);
    setFormData((prev) => ({ ...prev, sizes: arr }));
  };

  const handleColorsChange = (e) => {
    const val = e.target.value;
    const arr = val.split(",").map((c) => c.trim()).filter(c => c.length > 0);
    setFormData((prev) => ({ ...prev, colors: arr }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ ...product, ...formData });
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full space-y-4"
      >
        <h2 className="text-xl font-bold mb-2">Edit Product</h2>

        <div>
          <label className="block mb-1 font-semibold" htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            value={formData.name}
            onChange={handleChange}
            className="border px-3 py-2 rounded w-full"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold" htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="number"
            min="0"
            step="0.01"
            value={formData.price}
            onChange={handleChange}
            className="border px-3 py-2 rounded w-full"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold" htmlFor="sizes">Sizes (comma separated)</label>
          <input
            id="sizes"
            name="sizes"
            type="text"
            value={formData.sizes.join(", ")}
            onChange={handleSizesChange}
            className="border px-3 py-2 rounded w-full"
            placeholder="e.g. S, M, L, XL"
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold" htmlFor="colors">Colors (comma separated)</label>
          <input
            id="colors"
            name="colors"
            type="text"
            value={formData.colors.join(", ")}
            onChange={handleColorsChange}
            className="border px-3 py-2 rounded w-full"
            placeholder="e.g. Red, Blue, Green"
          />
        </div>

        <div className="flex justify-end gap-4 mt-4">
          <button
            type="button"
            onClick={onClose}
            className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400"
          >
            Cancel
          </button>
          <button
            type="submit"
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            Save
          </button>
        </div>
      </form>
    </div>
  );
};

export default AdminOrdersPage;
