import React from 'react';
import { useWishlist } from '../context/WishlistContext';
import { Link } from 'react-router-dom';

const Wishlist = () => {
  const { wishlistItems, removeFromWishlist } = useWishlist();

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">❤️ My Wishlist</h1>

      {wishlistItems.length === 0 ? (
        <div className="text-center text-gray-500">
          Your wishlist is empty.
          <br />
          <Link to="/products" className="text-blue-600 hover:underline">Browse products</Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {wishlistItems.map(item => (
            <div key={item._id} className="bg-white shadow rounded p-4">
              <img src={item.image} alt={item.name} className="w-full h-60 object-cover rounded mb-4" />
              <h2 className="text-lg font-semibold text-gray-800 mb-1">{item.name}</h2>
              <p className="text-sm text-gray-600 mb-2">₹{item.price}</p>
              <div className="flex justify-between">
                <Link to={`/product/${item._id}`} className="text-blue-600 hover:underline">View</Link>
                <button
                  onClick={() => removeFromWishlist(item._id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
