import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Navigation, Autoplay, Pagination } from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import '../styles/SwiperCustom.css';
SwiperCore.use([Navigation, Autoplay, Pagination]);

const ProductDetailPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [mainImage, setMainImage] = useState('');
  const [loading, setLoading] = useState(true);
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');

  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/products/${id}`);
        setProduct(res.data);
        setMainImage(res.data.image);
        setLoading(false);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }
    addToCart({ ...product, selectedSize, selectedColor });
    toast.success('Added to cart!');
  };

  const handleBuyNow = () => {
    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }
    addToCart({ ...product, selectedSize, selectedColor });
    toast.success('Item added! Redirecting to checkout...');
    navigate('/checkout');
  };

  if (loading) return <div className="text-center py-20">Loading product...</div>;
  if (!product) return <div className="text-center py-20">Product not found.</div>;

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <div className="flex flex-col md:flex-row gap-10">
        {/* Image gallery */}
        <div className="flex-1">
          <Swiper
            spaceBetween={10}
            slidesPerView={1}
            navigation
            pagination={{ clickable: true, type: 'bullets' }}
            autoplay={{ delay: 2500, disableOnInteraction: false }}
            className="custom-swiper"
            onSlideChange={(swiper) => setMainImage(product.images?.[swiper.activeIndex] || product.image)}
          >
            {(product.images?.length ? product.images : [product.image]).map((img, i) => (
              <SwiperSlide key={i}>
                <img src={img} alt={`${product.name}-${i}`} className="w-20 h-[500px] object-cover rounded shadow" />
              </SwiperSlide>
            ))}
          </Swiper>
        </div>

        {/* Details */}
        <div className="flex-1">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">{product.name}</h1>
          <p className="text-gray-600 mb-4">{product.description}</p>

          {/* Size Selection */}
          <div className="mb-4">
            <p className="font-medium text-gray-700 mb-2">Select Size:</p>
            <div className="flex gap-2">
              {['S', 'M', 'L', 'XL'].map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  className={`px-3 py-1 rounded border ${
                    selectedSize === size ? 'bg-black text-white' : 'bg-white text-black'
                  }`}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          {/* Color Selection */}
          <div className="mb-4">
            <p className="font-medium text-gray-700 mb-2">Select Color:</p>
            <div className="flex gap-2">
              {['Black', 'White', 'Blue', 'Red'].map(color => (
                <button
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  className={`px-3 py-1 rounded border ${
                    selectedColor === color ? 'bg-black text-white' : 'bg-white text-black'
                  }`}
                >
                  {color}
                </button>
              ))}
            </div>
          </div>

          <p className="text-xl font-semibold text-gray-800 mb-6">₹{product.price}</p>
          <p className="text-sm text-yellow-900 mb-4 italic">
             Unleash luxury & confidence with this exclusive Luxor style – made to turn heads.
          </p>

          <div className="flex gap-4">
            <button
              onClick={handleAddToCart}
              className="px-6 py-2 bg-black text-white rounded hover:bg-gray-800 transition"
            >
              Add to Cart
            </button>

            <button
              onClick={handleBuyNow}
              className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition disabled:opacity-50"
              disabled={!selectedSize}
            >
              Buy Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailPage;
