import React from 'react';

const wishlistItems = [
  {
    id: 1,
    name: 'Silk Satin Shirt',
    price: '$49.99',
    image: '/images/satin-shirt.jpg',
  },
  {
    id: 2,
    name: 'Linen Slim Trousers',
    price: '$59.99',
    image: '/images/linen-trousers.jpg',
  },
];

export default function Wishlist() {
  return (
    <div className="p-6">
      <h2 className="text-2xl font-semibold mb-6 text-center">Your Wishlist</h2>
      {wishlistItems.length === 0 ? (
        <p className="text-center text-gray-500">You have no items in your wishlist.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {wishlistItems.map((item) => (
            <div
              key={item.id}
              className="border rounded-2xl overflow-hidden shadow-sm hover:shadow-md transition duration-200"
            >
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-64 object-cover"
              />
              <div className="p-4">
                <h3 className="text-lg font-semibold mb-2">{item.name}</h3>
                <p className="text-gray-700 font-medium">{item.price}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
