// src/pages/OrderSuccessPage.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";

const OrderSuccessPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const order = location.state?.order; // âœ… we passed this from CheckoutPage

  return (
    <div className="max-w-3xl mx-auto text-center py-20 px-4">
      <h1 className="text-3xl font-bold mb-6 text-green-700">
        ðŸŽ‰ Order Placed Successfully!
      </h1>

      <p className="text-lg mb-8 text-gray-700">
        Thank you for shopping with <span className="font-semibold">Luxor</span>.
        <br />
        {order?._id && (
          <span>
            Your order ID is <span className="font-mono">{order._id}</span>.
          </span>
        )}
      </p>

      <div className="flex flex-col sm:flex-row gap-4 justify-center">
        {/* Continue Shopping */}
        <button
          onClick={() => navigate("/products")}
          className="bg-black text-white px-6 py-2 rounded hover:bg-gray-800 transition"
        >
          Continue Shopping
        </button>

        {/* Track My Order â†’ directly inside dashboard/orders */}
        <button
          onClick={() => navigate("/userdashboard/orders")}
          className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700 transition"
        >
          Track My Order
        </button>
      </div>
    </div>
  );
};

export default OrderSuccessPage;
