import React, { useState } from 'react';
import { useCart } from '../context/CartContext';
import { Trash2, Plus, Minus } from 'lucide-react';
import { Link } from 'react-router-dom';
import toast from 'react-hot-toast';

const CartPage = () => {
  const {
    cartItems,
    removeFromCart,
    increaseQuantity,
    decreaseQuantity,
    clearCart,
  } = useCart();

  const totalPrice = cartItems.reduce((acc, item) => acc + item.price * item.quantity, 0);

  const handleSizeChange = (id, size) => {
    const updatedItems = cartItems.map((item) =>
      item.id === id ? { ...item, size } : item
    );
    localStorage.setItem('luxor_cart', JSON.stringify(updatedItems));
    window.location.reload();
  };

  if (cartItems.length === 0) {
    return (
      <div className="text-center py-20">
        <h2 className="text-3xl font-semibold text-gray-700 mb-4">Your Cart is Empty</h2>
        <Link to="/products" className="text-blue-600 hover:underline">
          Browse Products
        </Link>
      </div>
    );
  }

  return (
    <div className="max-w-5xl mx-auto px-4 py-10">
      <h1 className="text-4xl font-bold mb-8 text-gray-800">ðŸ›’ Your Cart</h1>

      <div className="space-y-6">
        {cartItems.map((item) => (
          <div
            key={item.id}
            className="flex items-center justify-between bg-white shadow p-4 rounded-lg"
          >
            <div className="flex items-center gap-4">
              <img src={item.image} alt={item.name} className="w-20 h-28 object-cover rounded" />
              <div>
                <h2 className="text-lg font-semibold text-gray-800">{item.name}</h2>
                <p className="text-sm text-gray-500">{item.category} â€¢ {item.color}</p>
                <p className="text-sm text-gray-600">â‚¹{item.price}</p>
                <div className="mt-2">
                  <label className="text-sm mr-2">Size:</label>
                  <select
                    value={item.size || ''}
                    onChange={(e) => handleSizeChange(item.id, e.target.value)}
                    className="border px-2 py-1 rounded"
                  >
                    <option value="">Select Size</option>
                    <option value="S">S</option>
                    <option value="M">M</option>
                    <option value="L">L</option>
                    <option value="XL">XL</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <button onClick={() => {
                decreaseQuantity(item.id);
                toast.success('Decreased quantity');
              }} className="text-gray-600 hover:text-black">
                <Minus size={18} />
              </button>
              <span className="font-medium">{item.quantity}</span>
              <button onClick={() => {
                increaseQuantity(item.id);
                toast.success('Increased quantity');
              }} className="text-gray-600 hover:text-black">
                <Plus size={18} />
              </button>
              <button onClick={() => {
                removeFromCart(item.id);
                toast.success('Item removed from cart');
              }} className="text-red-500 hover:text-red-700 ml-4">
                <Trash2 size={20} />
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-10 flex flex-col md:flex-row justify-between items-center border-t pt-6">
        <div className="text-xl font-semibold text-gray-800">
          Total: â‚¹{totalPrice.toLocaleString()}
        </div>
        <div className="flex gap-4 mt-4 md:mt-0">
          <button
            onClick={() => {
              clearCart();
              toast.success('Cart cleared!');
            }}
            className="px-5 py-2 border border-gray-600 text-gray-700 rounded hover:bg-gray-100 transition"
          >
            Clear Cart
          </button>
          <button
            onClick={() => {
              const missingSize = cartItems.find(item => !item.size);
              if (missingSize) {
                toast.error('Please select a size for all items before checkout.');
                return;
              }
              window.location.href = '/checkout';
            }}
            className="px-6 py-2 bg-black text-white rounded hover:bg-gray-800 transition text-center"
          >
            Proceed to Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartPage;
