// src/context/CartContext.js
import React, { createContext, useContext, useReducer, useEffect } from 'react';

// Initial state
const initialState = {
  cartItems: [],
};

// Reducer
const cartReducer = (state, action) => {
  let newCartItems;
  switch (action.type) {
    case 'ADD_TO_CART': {
      // Always normalize size
      const payloadWithSize = {
        ...action.payload,
        size: action.payload.size || action.payload.selectedSize,
      };
      const exists = state.cartItems.find(
        item => item._id === payloadWithSize._id && item.size === payloadWithSize.size
      );
      if (exists) {
        newCartItems = state.cartItems.map(item =>
          item._id === payloadWithSize._id && item.size === payloadWithSize.size
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        newCartItems = [...state.cartItems, { ...payloadWithSize, quantity: 1 }];
      }
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return {
        ...state,
        cartItems: newCartItems,
      };
    }

    case 'REMOVE_FROM_CART': {
      newCartItems = state.cartItems.filter(
        item => !(item._id === action.payload._id && item.size === action.payload.size)
      );
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return {
        ...state,
        cartItems: newCartItems,
      };
    }

    case 'INCREASE_QUANTITY': {
      // Normalize size from payload
      const normSize = action.payload.size || action.payload.selectedSize;
      newCartItems = state.cartItems.map(item =>
        item._id === action.payload._id && item.size === normSize
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return {
        ...state,
        cartItems: newCartItems,
      };
    }

    case 'DECREASE_QUANTITY': {
      // Normalize size from payload
      const normSize = action.payload.size || action.payload.selectedSize;
      newCartItems = state.cartItems.map(item =>
        item._id === action.payload._id && item.size === normSize && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      );
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return {
        ...state,
        cartItems: newCartItems,
      };
    }

    case 'CLEAR_CART': {
      localStorage.setItem('cartItems', JSON.stringify([]));
      return {
        ...state,
        cartItems: [],
      };
    }

    case 'UPDATE_SIZE': {
      newCartItems = state.cartItems.map(item =>
        item._id === action.payload.id
          ? { ...item, size: action.payload.size }
          : item
      );
      localStorage.setItem('cartItems', JSON.stringify(newCartItems));
      return {
        ...state,
        cartItems: newCartItems,
      };
    }

    default: {
      return state;
    }
  }
};

// Context and Provider
const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(
    cartReducer,
    { cartItems: JSON.parse(localStorage.getItem('cartItems')) || [] }
  );

  useEffect(() => {
    localStorage.setItem('cartItems', JSON.stringify(state.cartItems));
  }, [state.cartItems]);

  console.log("ðŸ“¦ CartProvider initialized â€” dispatch type:", typeof dispatch);

  const addToCart = (product) => {
    const size = product.size || product.selectedSize;
    if (!product._id || !size) {
      console.warn("ðŸš« Cannot add to cart: Missing _id or size.");
      return;
    }
    // Always persist normalized size
    const normalizedProduct = { ...product, size };
    console.log("ðŸ›’ Product being added to cart:", normalizedProduct); // debug log
    dispatch({ type: 'ADD_TO_CART', payload: normalizedProduct });
  };

  const removeFromCart = (_id, size) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: { _id, size } });
  };

  const increaseQuantity = (_id, size) => {
    dispatch({ type: 'INCREASE_QUANTITY', payload: { _id, size } });
  };

  const decreaseQuantity = (_id, size) => {
    dispatch({ type: 'DECREASE_QUANTITY', payload: { _id, size } });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  const updateSize = (id, size) => {
    dispatch({ type: 'UPDATE_SIZE', payload: { id, size } });
  };

  return (
    <CartContext.Provider
  value={{
    state,
    dispatch,
    cartItems: state.cartItems,
    addToCart,
    removeFromCart,
    increaseQuantity,
    decreaseQuantity,
    clearCart,
    updateSize,
  }}
>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => useContext(CartContext);
