import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/orders/myorders', { withCredentials: true });
        setOrders(res.data);
      } catch (err) {
        setError('Failed to fetch orders');
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, []);

  if (loading) return <div className="text-center py-10">Loading orders...</div>;
  if (error) return <div className="text-center py-10 text-red-600">{error}</div>;
  if (orders.length === 0) return <div className="text-center py-10 text-gray-600">You haven’t placed any orders yet.</div>;

  return (
    <div className="max-w-5xl mx-auto p-6">
      <h1 className="text-3xl font-bold mb-6 text-center">Order History</h1>
      {orders.map((order) => (
        <div
          key={order._id}
          className="bg-white rounded-xl p-6 mb-6 border border-gray-200 hover:shadow-xl transition-shadow duration-300"
        >
          <div className="flex justify-between items-start mb-4">
            <div>
              <h2 className="text-xl font-bold mb-1 text-gray-800">Order #{order._id.slice(-6).toUpperCase()}</h2>
              <p className="text-sm text-gray-500">{new Date(order.createdAt).toLocaleDateString()}</p>
            </div>
            <button className="text-sm font-medium text-black border border-gray-300 px-4 py-1 rounded hover:bg-gray-100 transition">
              View Details
            </button>
          </div>
          <div className="text-sm space-y-1">
            <p><span className="font-medium text-gray-800">Items:</span> {order.items.map(i => i.name).join(', ')}</p>
            <p><span className="font-medium text-gray-800">Total:</span> ₹{order.totalPrice}</p>
            <p>
              <span className="font-medium text-gray-800">Status:</span>{' '}
              <span className={order.status === 'Delivered' ? 'text-green-600' : 'text-yellow-600'}>
                {order.status}
              </span>
            </p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default OrderHistory;
