import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import { useLocation, Link } from 'react-router-dom';
import axios from 'axios';
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Navigation, Autoplay, Pagination } from 'swiper';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import '../styles/SwiperCustom.css';
SwiperCore.use([Navigation, Autoplay, Pagination]);

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState('All');
  const [colorFilter, setColorFilter] = useState('All');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const [selectedSizes, setSelectedSizes] = useState({});

  const location = useLocation();
  const urlSearchParams = new URLSearchParams(location.search);
  const initialSearch = urlSearchParams.get('search') || '';
  const [searchTerm, setSearchTerm] = useState(initialSearch);

  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const res = await axios.get('http://localhost:3001/api/products');
        setProducts(res.data);
      } catch (error) {
        console.error("Error fetching products:", error);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    console.log("addToCart function:", addToCart); // Debugging line
    addToCart(product);
  };

  const filteredProducts = products.filter((product) => {
    const matchesCategory = filter === 'All' || product.category === filter;
    const matchesColor = colorFilter === 'All' || product.color === colorFilter;
    const matchesPrice = (!minPrice || product.price >= parseInt(minPrice)) &&
                         (!maxPrice || product.price <= parseInt(maxPrice));
    return matchesCategory && matchesColor && matchesPrice;
  });

  return (
    <div className="px-6 py-10 bg-gray-100 min-h-screen">
      <h1 className="text-4xl font-bold text-center mb-10 text-gray-800">Luxor Collection</h1>

      {/* Filters */}
      <div className="flex flex-wrap gap-4 justify-center mb-10">
        <select
          className="px-4 py-2 rounded border text-gray-700"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="All">All</option>
          <option value="Men">Men</option>
          <option value="Women">Women</option>
        </select>

        <select
          className="px-4 py-2 rounded border text-gray-700"
          value={colorFilter}
          onChange={(e) => setColorFilter(e.target.value)}
        >
          <option value="All">All Colors</option>
          <option value="White">White</option>
          <option value="Black">Black</option>
          <option value="Blue">Blue</option>
          <option value="Red">Red</option>
        </select>

        <input
          type="number"
          placeholder="Min Price"
          className="px-4 py-2 rounded border text-gray-700"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />

        <input
          type="number"
          placeholder="Max Price"
          className="px-4 py-2 rounded border text-gray-700"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
        {filteredProducts.map((product) => (
          <div
            key={product._id}
            className="bg-white shadow-lg rounded-lg overflow-hidden hover:shadow-2xl transition duration-300"
          >
            <Link
              to={`/product/${product._id}`}
              className="block"
            >
              <Swiper
                spaceBetween={10}
                slidesPerView={1}
                navigation
                autoplay={{ delay: 2500, disableOnInteraction: false }}
                pagination={{ clickable: true, type: 'bullets' }}
                className="w-full h-72 custom-swiper"
              >
                {(product.images?.length ? product.images : [product.image]).map((img, i) => (
                  <SwiperSlide key={i}>
                    <img src={img} alt={`${product.name}-${i}`} className="w-full h-72 object-cover" />
                  </SwiperSlide>
                ))}
              </Swiper>
              <div className="p-4">
                <h2 className="text-lg font-semibold text-gray-800 mb-1">{product.name}</h2>
                <p className="text-sm text-gray-500 mb-1">{product.category} • {product.color}</p>
                <p className="text-sm text-gray-700 font-semibold mb-3">₹{product.price.toLocaleString()}</p>
              </div>
            </Link>
            <div className="p-4 pt-0">
              <select
                className="w-full mb-3 px-4 py-2 rounded border text-gray-700"
                value={selectedSizes[product._id] || ''}
                onChange={(e) =>
                  setSelectedSizes((prev) => ({
                    ...prev,
                    [product._id]: e.target.value,
                  }))
                }
              >
                <option value="">Select Size</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>
              <button
                onClick={() => {
                  const selectedSize = selectedSizes[product._id];
                  if (!selectedSize) {
                    alert('Please select a size before adding to cart.');
                    return;
                  }
                  addToCart({ ...product, size: selectedSize });
                }}
                className="w-full bg-black text-white py-2 rounded hover:bg-gray-800 transition"
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
