import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';
import { useWishlist } from '../context/WishlistContext';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const ProductDetailPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [mainImage, setMainImage] = useState('');
  const [loading, setLoading] = useState(true);
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedColor, setSelectedColor] = useState('');
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');

  const { addToCart } = useCart();
  const { wishlistItems, addToWishlist } = useWishlist();
  const navigate = useNavigate();

  const handleAddToWishlist = () => {
    if (!wishlistItems.find(item => item._id === product._id)) {
      addToWishlist(product);
      toast.success('Added to wishlist!');
    } else {
      toast('Already in wishlist!');
    }
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const res = await axios.get(`http://localhost:3001/api/products/${id}`);
        setProduct(res.data);
        setMainImage(res.data.image);
        setLoading(false);
      } catch (err) {
        console.error(err);
      }
    };
    fetchProduct();
  }, [id]);

  const handleAddToCart = () => {
    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }
    addToCart({ ...product, size: selectedSize, color: selectedColor });
    toast.success('Added to cart!');
  };

  const handleBuyNow = () => {
    if (!selectedSize) {
      toast.error('Please select a size');
      return;
    }
    addToCart({ ...product, size: selectedSize, color: selectedColor });
    toast.success('Item added! Redirecting to checkout...');
    navigate('/checkout');
  };

  if (loading) return <div className="text-center py-20">Loading product...</div>;
  if (!product) return <div className="text-center py-20">Product not found.</div>;

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <div className="flex flex-col md:flex-row gap-10">
        {/* Image gallery */}
        <div className="flex-1 relative">
          {/* ✅ Stock Badge */}
          {product.countInStock === 0 && (
            <span className="absolute top-2 left-2 bg-red-600 text-white text-xs px-2 py-1 rounded">
              Out of Stock
            </span>
          )}
          {product.countInStock > 0 && product.countInStock < 10 && (
            <span className="absolute top-2 left-2 bg-yellow-500 text-white text-xs px-2 py-1 rounded">
              Only {product.countInStock} left
            </span>
          )}

          <img
            src={mainImage}
            alt={product.name}
            className="w-full h-[500px] object-cover rounded shadow"
          />
          <div className="flex gap-4 mt-4 overflow-x-auto pb-2 scrollbar-hide">
            {product.images?.map((img, i) => (
              <img
                key={i}
                src={img}
                alt={`thumb-${i}`}
                className={`min-w-[80px] w-20 h-24 object-cover cursor-pointer border-2 transition-all duration-200 ${
                  mainImage === img ? 'border-black scale-105 shadow-md' : 'border-gray-300 hover:border-black'
                }`}
                onClick={() => setMainImage(img)}
              />
            ))}
          </div>
        </div>

        {/* Details */}
        <div className="flex-1">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">{product.name}</h1>
          <p className="text-gray-600 mb-4">{product.description}</p>

          {/* ✅ Stock info inline */}
          <p className="text-sm mb-4">
            {product.countInStock > 0
              ? `In Stock: ${product.countInStock}`
              : "Currently Out of Stock"}
          </p>

          {/* Size Selection */}
          <div className="mb-4">
            <p className="font-medium text-gray-700 mb-2">Select Size:</p>
            <div className="flex gap-2">
              {['S', 'M', 'L', 'XL'].map(size => (
                <button
                  key={size}
                  onClick={() => setSelectedSize(size)}
                  disabled={product.countInStock === 0}
                  className={`px-3 py-1 rounded border ${
                    selectedSize === size ? 'bg-black text-white' : 'bg-white text-black'
                  } ${product.countInStock === 0 ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  {size}
                </button>
              ))}
            </div>
          </div>

          {/* Color Selection */}
          <div className="mb-4">
            <p className="font-medium text-gray-700 mb-2">Select Color:</p>
            <div className="flex gap-2">
              {['Black', 'White', 'Blue', 'Red'].map(color => (
                <button
                  key={color}
                  onClick={() => setSelectedColor(color)}
                  disabled={product.countInStock === 0}
                  className={`px-3 py-1 rounded border ${
                    selectedColor === color ? 'bg-black text-white' : 'bg-white text-black'
                  } ${product.countInStock === 0 ? 'opacity-50 cursor-not-allowed' : ''}`}
                >
                  {color}
                </button>
              ))}
            </div>
          </div>

          <p className="text-xl font-semibold text-gray-800 mb-6">₹{product.price}</p>
          <p className="text-sm text-yellow-900 mb-4 italic">
             Unleash luxury & confidence with this exclusive Luxor style – made to turn heads.
          </p>

          <div className="flex gap-4">
            <button
              onClick={handleAddToCart}
              disabled={product.countInStock === 0}
              className={`px-6 py-2 rounded transition ${
                product.countInStock === 0
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-black text-white hover:bg-gray-800'
              }`}
            >
              Add to Cart
            </button>

            <button
              onClick={handleBuyNow}
              disabled={!selectedSize || product.countInStock === 0}
              className={`px-6 py-2 rounded transition ${
                !selectedSize || product.countInStock === 0
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'bg-green-600 text-white hover:bg-green-700'
              }`}
            >
              Buy Now
            </button>
          </div>
          <button
            onClick={handleAddToWishlist}
            className="mt-4 px-6 py-2 border border-grey-600 text-black-600 rounded hover:bg-grey-50 transition"
          >
             Add to Wishlist
          </button>
        </div>
      </div>

      {/* Reviews Section */}
      <div className="mt-10">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">Customer Reviews</h2>

        {product.reviews && product.reviews.length > 0 ? (
          <div className="space-y-4">
            {product.reviews.map((review, index) => (
              <div key={index} className="border p-4 rounded bg-white shadow-sm">
                <div className="flex justify-between items-center">
                  <h4 className="font-semibold">{review.name}</h4>
                  <span className="text-sm text-green-600">Rating: {review.rating}/5</span>
                </div>
                <p className="text-sm text-gray-700 mt-1">{review.comment}</p>
              </div>
            ))}
          </div>
        ) : (
          <p className="text-gray-500">No reviews yet.</p>
        )}
      </div>

      {/* Submit Review Section */}
      <div className="mt-10">
        <h3 className="text-xl font-semibold text-gray-800 mb-4">Write a Review</h3>
        <form
          onSubmit={async (e) => {
            e.preventDefault();
            if (!rating || !comment) {
              toast.error('Please fill all review fields');
              return;
            }
            try {
              await axios.post(
                `http://localhost:3001/api/products/${id}/reviews`,
                { rating: Number(rating), comment },
                { withCredentials: true }
              );
              toast.success('Review submitted!');
              setRating(0);
              setComment('');
              const res = await axios.get(`http://localhost:3001/api/products/${id}`);
              setProduct(res.data);
            } catch (err) {
              toast.error(err.response?.data?.message || 'Error submitting review');
            }
          }}
          className="space-y-4"
        >
          <div>
            <label className="block text-sm font-medium text-gray-700">Rating:</label>
            <select
              value={rating}
              onChange={(e) => setRating(e.target.value)}
              className="mt-1 block w-full px-4 py-2 border rounded"
            >
              <option value="">Select rating</option>
              {[1, 2, 3, 4, 5].map(r => (
                <option key={r} value={r}>{r} - {['Poor','Fair','Good','Very Good','Excellent'][r-1]}</option>
              ))}
            </select>
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Comment:</label>
            <textarea
              value={comment}
              onChange={(e) => setComment(e.target.value)}
              rows="3"
              className="mt-1 block w-full px-4 py-2 border rounded"
            />
          </div>
          <button
            type="submit"
            className="px-6 py-2 bg-black text-white rounded hover:bg-gray-800 transition"
          >
            Submit Review
          </button>
        </form>
      </div>
    </div>
  );
};

export default ProductDetailPage;
