import React, { useState, useEffect } from 'react';
import { useWishlist } from '../context/WishlistContext';
import { useCart } from '../context/CartContext';
import { Link } from 'react-router-dom';
import toast from 'react-hot-toast';
import axios from 'axios';

const Wishlist = () => {
  const { wishlistItems, removeFromWishlist } = useWishlist();
  const { addToCart } = useCart();
  const [selectedSizes, setSelectedSizes] = useState({});
  const [liveWishlist, setLiveWishlist] = useState(wishlistItems);

  // ‚úÖ Engagement notification after 10s
  useEffect(() => {
    const timer = setTimeout(() => {
      if (wishlistItems.length > 0) {
        toast("Others Are Eyeing Your Picks‚ÄîSnag ‚ÄòEm Before They‚Äôre Gone!", {
          icon: "ü´†",
        });
      }
    }, 10000);

    return () => clearTimeout(timer);
  }, [wishlistItems]);

  // ‚úÖ Sync stock + auto-remove inactive products (debounced)
useEffect(() => {
  const syncStock = async () => {
    try {
      const updatedItems = await Promise.all(
        wishlistItems.map(async (item) => {
          try {
            const res = await axios.get(`http://localhost:3001/api/products/${item._id}`);
            const liveProduct = res.data;

            if (!liveProduct.active) {
              removeFromWishlist(item._id);
              toast.error(`"${item.name}" is no longer available and was removed from your wishlist.`);
              return null;
            }

            return {
              ...item,
              countInStock: liveProduct.countInStock,
              active: liveProduct.active,
            };
          } catch {
            return item; // fallback
          }
        })
      );

      setLiveWishlist(updatedItems.filter(Boolean));
    } catch (err) {
      console.error("‚ùå Error syncing wishlist stock:", err);
    }
  };

  // ‚úÖ debounce (wait 500ms after wishlist changes)
  const debounceTimer = setTimeout(() => {
    syncStock();
  }, 500);

  // ‚úÖ still keep auto-refresh every 30s
  const interval = setInterval(syncStock, 30000);

  return () => {
    clearTimeout(debounceTimer);
    clearInterval(interval);
  };
}, [wishlistItems, removeFromWishlist]);

  // ‚úÖ Check live stock before moving to cart
  const handleMoveToCart = async (item) => {
    const selectedSize = selectedSizes[item._id];
    if (!selectedSize) {
      toast.error(`Please select a size for "${item.name}"`);
      return;
    }

    try {
      const res = await axios.get(`http://localhost:3001/api/products/${item._id}`);
      const liveProduct = res.data;

      if (!liveProduct.active || liveProduct.countInStock <= 0) {
        toast.error(`"${item.name}" is currently out of stock`);
        return;
      }

      addToCart({ ...item, size: selectedSize, countInStock: liveProduct.countInStock });
      removeFromWishlist(item._id);
      toast.success(`Moved "${item.name}" to cart`);
    } catch (err) {
      console.error("‚ùå Stock check failed:", err);
      toast.error("Could not verify stock, please try again");
    }
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-10">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">‚ù§Ô∏è My Wishlist</h1>

      {liveWishlist.length === 0 ? (
        <div className="text-center text-gray-500">
          Your wishlist is empty.
          <br />
          <Link to="/products" className="text-blue-600 hover:underline">Browse products</Link>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {liveWishlist.map(item => (
            <div key={item._id} className="bg-white shadow rounded p-4 relative">
              {/* ‚úÖ Stock Badge */}
              {item.countInStock === 0 && (
                <span className="absolute top-2 left-2 bg-red-600 text-white text-xs px-2 py-1 rounded">
                  Out of Stock
                </span>
              )}
              {item.countInStock > 0 && item.countInStock < 10 && (
                <span className="absolute top-2 left-2 bg-yellow-500 text-white text-xs px-2 py-1 rounded">
                  Only {item.countInStock} left
                </span>
              )}

              <img src={item.image} alt={item.name} className="w-full h-60 object-cover rounded mb-4" />
              <h2 className="text-lg font-semibold text-gray-800 mb-1">{item.name}</h2>
              <p className="text-sm text-gray-600 mb-2">‚Çπ{item.price}</p>
              <p className="text-xs text-gray-500">
                {item.countInStock > 0 ? `In Stock: ${item.countInStock}` : "Out of Stock"}
              </p>

              {/* ‚úÖ Size selection */}
              <select
                value={selectedSizes[item._id] || ''}
                onChange={(e) =>
                  setSelectedSizes((prev) => ({
                    ...prev,
                    [item._id]: e.target.value,
                  }))
                }
                className="w-full mb-3 px-3 py-2 border rounded text-gray-700"
              >
                <option value="">Select Size</option>
                <option value="S">S</option>
                <option value="M">M</option>
                <option value="L">L</option>
                <option value="XL">XL</option>
              </select>

              <div className="flex justify-between items-center">
                <Link to={`/product/${item._id}`} className="text-blue-600 hover:underline">
                  View
                </Link>

                {/* ‚úÖ Disable if out of stock */}
                <button
                  onClick={() => handleMoveToCart(item)}
                  disabled={item.countInStock <= 0}
                  className={`px-3 py-1 rounded transition ${
                    item.countInStock <= 0
                      ? "bg-gray-300 text-gray-500 cursor-not-allowed"
                      : "bg-black text-white hover:bg-gray-800"
                  }`}
                >
                  Move to Cart
                </button>

                <button
                  onClick={() => removeFromWishlist(item._id)}
                  className="text-red-500 hover:text-red-700"
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
